mapper.update:
update s_con_bu_type_account_category
client_id = #{clientId}, fin_shoufukuan_type_code = #{finShoufukuanTypeCode}, bu_type_code = #{buTypeCode}, account_category_code = #{accountCategoryCode}, book_type_code = #{bookTypeCode}, book_source_category = #{bookSourceCategory}, sort = #{sort}, remark = #{remark}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where sid = #{sid}

update.foreach:
update s_con_bu_type_account_category
client_id = #{clientId}, fin_shoufukuan_type_code = #{finShoufukuanTypeCode}, bu_type_code = #{buTypeCode}, account_category_code = #{accountCategoryCode}, book_type_code = #{bookTypeCode}, book_source_category = #{bookSourceCategory}, sort = #{sort}, remark = #{remark}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where sid = #{sid}

mapper.select:
select t.client_id, t.sid, t.fin_shoufukuan_type_code, t.bu_type_code, t.account_category_code, t.book_type_code, t.book_source_category, t.sort, t.data_source_sys, if(t.fin_shoufukuan_type_code = 'SK',t5.name,t6.name) as bu_type_name, t7.name as account_category_name, t8.name as book_type_name, t9.name as book_source_category_name from s_con_bu_type_account_category t LEFT JOIN s_con_bu_type_receivable_bill t5 ON t.bu_type_code = t5.code AND t.fin_shoufukuan_type_code = 'SK' LEFT JOIN s_con_bu_type_pay_bill t6 ON t.bu_type_code = t6.code AND t.fin_shoufukuan_type_code = 'FK' LEFT JOIN s_con_account_category t7 ON t.account_category_code = t7.code LEFT JOIN s_con_book_type t8 ON t.book_type_code = t8.code LEFT JOIN s_con_book_source_category t9 ON t.book_source_category = t9.code
and t.client_id = #{clientId}
and t.fin_shoufukuan_type_code = #{finShoufukuanTypeCode}
and t.bu_type_code = #{buTypeCode}
and t.bu_type_code in (#{buTypeCode})
and t.account_category_code = #{accountCategoryCode}
and t.account_category_code in (#{accountCategoryCode})
and t.book_type_code = #{bookTypeCode}
and t.book_type_code in (#{bookTypeCode})
and t.book_source_category = #{bookTypeCode}
and t.book_source_category in (#{bookSourceCategory})
and t.sort =#{sort}
and t.handle_status = #{handleStatus}
and (t.creator_account like concat('%', #{item}, '%') )

select t.client_id, t.sid,
       t.code as account_category_code,
       t.name as account_category_name,
       t.status, t.handle_status
from s_con_account_category t


        and t.client_id = #{clientId}


        and t.status = #{status}


        and t.handle_status = #{handleStatus}

select t.fin_shoufukuan_type_code, t.bu_type_code, t.account_category_code, t.book_type_code, if(t.fin_shoufukuan_type_code = 'SK',t5.name,t6.name) as bu_type_name, t7.name as account_category_name, t8.name as book_type_name from s_con_bu_type_account_category t LEFT JOIN s_con_bu_type_receivable_bill t5 ON t.bu_type_code = t5.code AND t.fin_shoufukuan_type_code = 'SK' LEFT JOIN s_con_bu_type_pay_bill t6 ON t.bu_type_code = t6.code AND t.fin_shoufukuan_type_code = 'FK' LEFT JOIN s_con_account_category t7 ON t.account_category_code = t7.code LEFT JOIN s_con_book_type t8 ON t.book_type_code = t8.code
and t.client_id = #{clientId}
and t.fin_shoufukuan_type_code = #{finShoufukuanTypeCode}
and t.bu_type_code = #{buTypeCode}
and t.bu_type_code in (#{buTypeCode})
and t.account_category_code = #{accountCategoryCode}
and t.account_category_code in (#{accountCategoryCode})
and t.book_type_code = #{bookTypeCode}
and t.book_type_code in (#{bookTypeCode})
and t.handle_status = #{handleStatus}
group by t.book_type_code order by t8.name asc
