mapper.select:



        and t.client_id = #{clientId}


        and t.sid in
        (#{item})


        and t.sid= #{sid}


        and (t.code like concat('%',
        #{item}, '%')
    )

    and
        t.name like concat('%', #{name}, '%')


        and t.delivery_category in
        (#{item})


        and t.delivery_category = #{deliveryCategory}


        and t.chuku_category = #{chukuCategory}


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


        and (t1.nick_name like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        and (t.is_client_define like
        concat('%',
        #{item}, '%')
    )


        and t.sort =#{sort}


        ${params.dataScope}


order by t.sort asc, t.name asc

mapper.update:
update s_con_bu_type_delivery_note
code = #{code}, name = #{name}, delivery_category = #{deliveryCategory}, chuku_category = #{chukuCategory}, is_client_define = #{isClientDefine}, sort = #{sort}, status = #{status}, handle_status = #{handleStatus}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where sid = #{sid}

update.foreach:
update s_con_bu_type_delivery_note
code = #{code}, name = #{name}, delivery_category = #{deliveryCategory}, chuku_category = #{chukuCategory}, is_client_define = #{isClientDefine}, sort = #{sort}, status = #{status}, handle_status = #{handleStatus}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where sid = #{sid}

mapper.select:
select sid,code,name,delivery_category,chuku_category,status,handle_status,create_date,sort from s_con_bu_type_delivery_note
where status = '1' and handle_status = '5' and delivery_category=#{deliveryCategory}
order by sort asc, name asc
