mapper.select:



        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.sid in
        (#{item})


        and t.sid= #{sid}


        and (t.code like concat('%',
        #{item}, '%')
    )

    and
        t.name like concat('%', #{name}, '%')


        and t.delivery_type = #{deliveryType}


        and (t.status like concat('%',
        #{item}, '%')
    )


        and (t.handle_status like
        concat('%',
        #{item}, '%')
    )


        and (t1.nick_name like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        and (t.is_client_define like
        concat('%',
        #{item}, '%')
    )


        and t.sort =#{sort}


        and t.inventory_control_mode = #{inventoryControlMode}


        and t.yutou_anxu_type = #{yutouAnxuType}


        and t.is_edit_price = #{isEditPrice}


        and t.is_once_purchase = #{isOncePurchase}


        and t.is_reserve_stock = #{isReserveStock}


        ${params.dataScope}


order by t.sort asc, t.name asc

mapper.update:
update s_con_bu_type_purchase_order
code = #{code}, name = #{name}, delivery_type = #{deliveryType}, toexpire_days = #{toexpireDays}, remark = #{remark}, delivery_type = #{deliveryType}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, is_client_define = #{isClientDefine}, sort = #{sort}, inventory_control_mode = #{inventoryControlMode}, yutou_anxu_type = #{yutouAnxuType}, is_edit_price = #{isEditPrice}, is_reserve_stock = #{isReserveStock}, is_once_purchase = #{isOncePurchase},
where sid = #{sid}

update.foreach:
update s_con_bu_type_purchase_order
client_id = #{clientId}, code = #{code}, name = #{name}, delivery_type = #{deliveryType}, toexpire_days = #{toexpireDays}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, is_client_define = #{isClientDefine}, sort = #{sort}, inventory_control_mode = #{inventoryControlMode}, yutou_anxu_type = #{yutouAnxuType}, is_edit_price = #{isEditPrice}, is_reserve_stock = #{isReserveStock}, is_once_purchase = #{isOncePurchase},
where sid = #{sid}

mapper.select:
select client_id, sid, code, name, status, handle_status, creator_account, create_date, inventory_control_mode, yutou_anxu_type, delivery_type, is_edit_price, is_reserve_stock, is_once_purchase, sort from s_con_bu_type_purchase_order
and status = #{status}
and handle_status = #{handleStatus}
and code in (#{code})
order by sort asc, name asc
