mapper.select:



        and t.client_id = #{clientId}


        and t.sid in
        (#{item})


        and t.sid= #{sid}


        and t.code like concat('%', #{code}, '%')

    and
        t.name like concat('%', #{name}, '%')


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


        and t1.nick_name  like concat('%', #{creatorAccount}, '%')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account like concat('%', #{confirmerAccount}, '%')


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        andt.is_client_define = #{isClientDefine}


        and t.sort =#{sort}


        ${params.dataScope}


order by t.sort asc, t.name asc

mapper.update:
update s_con_business_channel
code = #{code}, name = #{name}, is_client_define = #{isClientDefine}, sort = #{sort}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where sid = #{sid}

update.foreach:
update s_con_business_channel
client_id = #{o.clientId}, code = #{o.code}, name = #{o.name}, is_client_define = #{o.isClientDefine}, sort = #{o.sort}, remark = #{o.remark}, status = #{o.status}, handle_status = #{o.handleStatus}, creator_account = #{o.creatorAccount}, create_date = #{o.createDate}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, confirmer_account = #{o.confirmerAccount}, confirm_date = #{o.confirmDate}, data_source_sys = #{o.dataSourceSys},
where sid = #{o.sid}

mapper.select:
select sid,code,name,status,handle_status,create_date,sort from s_con_business_channel


        and handle_status = #{handleStatus}


        and status = #{status}


order by sort asc, name asc
