mapper.select:



        and t.client_id = #{clientId}


        and t.sid in
        (#{item})


        and t.sid= #{sid}


        and (t.code like concat('%',
        #{item}, '%')
    )

    and
        t.name like concat('%', #{name}, '%')


        and t.database_table_name = #{databaseTableName}


        and t.data_level = #{dataLevel}

    and
        t.code_mode like concat('%', #{codeMode}, '%')

    and
        t.code_standard like concat('%', #{codeStandard}, '%')


        and t.sort =#{sort}


        and t.is_client_define = #{isClientDefine}


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


        and (t1.nick_name like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.sort asc, t.name asc

mapper.update:
update s_con_dataobject_category
client_id = #{clientId}, sid = #{sid}, code = #{code}, name = #{name}, database_table_name = #{databaseTableName}, data_level = #{dataLevel}, code_mode = #{codeMode}, code_standard = #{codeStandard}, sort = #{sort}, is_client_define = #{isClientDefine}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where sid = #{sid}

update.foreach:
update s_con_dataobject_category
client_id = #{o.clientId}, sid = #{o.sid}, code = #{o.code}, name = #{o.name}, database_table_name = #{o.databaseTableName}, dataLevel = #{o.dataLevel}, code_mode = #{o.codeMode}, code_standard = #{o.codeStandard}, sort = #{o.sort}, is_client_define = #{o.isClientDefine}, remark = #{o.remark}, status = #{o.status}, handle_status = #{o.handleStatus}, creator_account = #{o.creatorAccount}, create_date = #{o.createDate}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, confirmer_account = #{o.confirmerAccount}, confirm_date = #{o.confirmDate}, data_source_sys = #{o.dataSourceSys},
where sid = #{o.sid}

mapper.select:
select sid,code,name,sort,status,handle_status,sort, database_table_name , data_level from s_con_dataobject_category


        and status = #{status}


        and handle_status = #{handleStatus}


        and code_mode = #{codeMode}


        and code_standard = #{codeStandard}


        and database_table_name = #{databaseTableName}


        and data_level =  #{dataLevel}


order by sort asc, name asc
