mapper.select:



       and t.client_id = #{clientId}


       and t.dataobject_category_sid =#{dataobjectCategorySid}


       and t.dataobject_category_code = #{dataobjectCategoryCode}


       and t.dataobject_handle_status like concat("%" , #{dataobjectHandleStatus} , "%")


       and t.dataobject_category_sid in
       (#{dataobjectCategorySid})


       and t.dataobject_handle_status in
       (#{handleStatus})


       and (t.creator_account
       like concat('%',
       #{item}, '%')
   )


       and t2.nick_name like concat('%',#{creatorAccountName}, '%')


       and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


       and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


       and (t.confirmer_account
       like concat('%',
       #{item}, '%')
   )


       and t.confirm_date = #{confirmDate}


       and (t.data_source_sys
       like concat('%',
       #{item}, '%')
   )


       ${params.dataScope}

order by t.dataobject_category_code asc

select t.dataobject_category_sid, t.dataobject_category_code, t1.name as dataobject_category_name from s_con_dataobject_handle_status t LEFT JOIN s_con_dataobject_category t1 ON t.dataobject_category_sid = t1.sid LEFT JOIN sys_user t2 ON t2.user_name = t.creator_account
and t.client_id = #{clientId}
and t.dataobject_category_sid =#{dataobjectCategorySid}
and t.dataobject_category_code = #{dataobjectCategoryCode}
and t.dataobject_handle_status = #{dataobjectHandleStatus}
and t.dataobject_handle_status in (#{handleStatus})
and (t.creator_account like concat('%', #{item}, '%') )
and (t2.nick_name like concat('%', #{item}, '%') )
and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')
and (t.data_source_sys like concat('%', #{item}, '%') )
${params.dataScope}
group by t.dataobject_category_sid order by t.dataobject_category_code asc

mapper.update:
update s_con_dataobject_handle_status
dataobject_category_sid = #{dataobjectCategorySid}, dataobject_category_code = #{dataobjectCategoryCode}, dataobject_handle_status = #{dataobjectHandleStatus}, remark = #{remark}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where sid = #{sid}

update.foreach:
update s_con_dataobject_handle_status
dataobject_category_sid = #{dataobjectCategorySid}, dataobject_category_code = #{dataobjectCategoryCode}, dataobject_handle_status = #{dataobjectHandleStatus}, remark = #{remark}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where sid = #{sid}
