mapper.select:



        and t.doc_type_code in
        (#{docTypeCode})


        and t.bu_type_code in
        (#{buTypeCode})


        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.sid =#{sid}


        and t.doc_type_code = #{docTypeCode}


        and t.bu_type_code = #{buTypeCode}


        and t.is_non_approval = #{isNonApproval}


        and t.sort =#{sort}


        and (t.status like concat('%',
        #{item}, '%')
    )


        and (t.handle_status like
        concat('%',
        #{item}, '%')
    )


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.sort asc, t2.name asc, t3.name asc

mapper.update:
update s_con_doc_bu_type_group_po
client_id = #{clientId}, sid = #{sid}, doc_type_code = #{docTypeCode}, bu_type_code = #{buTypeCode}, is_non_approval = #{isNonApproval}, sort = #{sort}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where sid = #{sid}

update.foreach:
update s_con_doc_bu_type_group_po
