mapper.select:



        and t.client_id = #{clientId}


        and (t.code like concat('%',
        #{item}, '%')
    )


        and t.name like concat('%', #{name}, '%')


        and t.is_client_define = #{isClientDefine}


        and t.sort =#{sort}


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{handleStatus})


        and t.creator_account = #{creatorAccount}


        and t1.nick_name like concat('%', #{creatorAccountName}, '%')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.create_date desc

mapper.update:
update s_con_doc_type_manufacture_outsource_settle
code = #{code}, name = #{name}, is_client_define = #{isClientDefine}, sort = #{sort}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where sid = #{sid}

update.foreach:
update s_con_doc_type_manufacture_outsource_settle
code = #{o.code}, name = #{o.name}, is_client_define = #{o.isClientDefine}, sort = #{o.sort}, remark = #{o.remark}, status = #{o.status}, handle_status = #{o.handleStatus}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, confirmer_account = #{o.confirmerAccount}, confirm_date = #{o.confirmDate}, data_source_sys = #{o.dataSourceSys},
where sid = #{o.sid}

mapper.select:
select client_id, sid, code, name, status, handle_status, creator_account, sort, create_date from s_con_doc_type_manufacture_outsource_settle
and handle_status = #{handleStatus}
and handle_status in (#{handleStatus})
and status = #{status}
order by sort asc, name asc
