mapper.select:



        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.sid in
        (#{item})


        and t.sid =#{sid}


        and (t.code like concat('%',
        #{item}, '%')
    )

    and
        t.name like concat('%', #{name}, '%')


        and t.dataobject_category_sid =#{dataobjectCategorySid}


        and t.dataobject_category_code = #{dataobjectCategoryCode}


        and (t.status like concat('%',
        #{item}, '%')
    )


        and (t.handle_status like
        concat('%',
        #{item}, '%')
    )


        and (t1.nick_name  like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        and t.sort =#{sort}


        and t.is_upload_multifile = #{isUploadMultifile}


        ${params.dataScope}


order by t.sort asc, t.name asc

mapper.update:
update s_con_file_type
code = #{code}, name = #{name}, max_size = #{maxSize}, dataobject_category_sid = #{dataobjectCategorySid}, dataobject_category_code = #{dataobjectCategoryCode}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, sort = #{sort}, is_upload_multifile = #{isUploadMultifile},
where sid = #{sid}

update.foreach:
update s_con_file_type
client_id = #{clientId}, code = #{code}, name = #{name}, max_size = #{maxSize}, dataobject_category_sid = #{dataobjectCategorySid}, dataobject_category_code = #{dataobjectCategoryCode}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, sort = #{sort}, is_upload_multifile = #{isUploadMultifile},
where sid = #{sid}
