mapper.select:



        and (t.is_storehouse_edit
        = #{item})


        and (
        t.is_storehouse_location_edit = #{item})


        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.sid in
        (#{item})


        and t.sid =#{sid}


        and t.inv_doc_category_code = #{invDocCategoryCode}


        and t.movement_type_code = #{movementTypeCode}


        and t.doc_category_code = #{docCategoryCode}


        and remark like #{remark}


        and (t.status like concat('%',
        #{item}, '%')
    )


        and (t.handle_status like
        concat('%',
        #{item}, '%')
    )


        and (t1.nick_name  like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        and (t.is_client_define like
        concat('%',
        #{item}, '%')
    )


        and t.sort =#{sort}


        ${params.dataScope}


order by t.sort asc, t2.name asc

mapper.update:
update s_con_in_out_stock_doc_category
inv_doc_category_code = #{invDocCategoryCode}, movement_type_code = #{movementTypeCode}, doc_category_code = #{docCategoryCode}, is_storehouse_edit = #{isStorehouseEdit}, is_storehouse_location_edit = #{isStorehouseLocationEdit}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, is_client_define = #{isClientDefine}, sort = #{sort},
where sid = #{sid}

update.foreach:
update s_con_in_out_stock_doc_category
client_id = #{clientId}, inv_doc_category_code = #{invDocCategoryCode}, movement_type_code = #{movementTypeCode}, doc_category_code = #{docCategoryCode}, is_storehouse_edit = #{isStorehouseEdit}, is_storehouse_location_edit = #{isStorehouseLocationEdit}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, is_client_define = #{isClientDefine}, sort = #{sort},
where sid = #{sid}
