mapper.select:



        and t.client_id = #{clientId}


        and t.sid in
        (#{item})


        and t.sid= #{sid}


        and (t.code like concat('%',
        #{item}, '%')
    )

    and
        t.name like concat('%', #{name}, '%')


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


        and (t2.nick_name like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        and t.is_client_define = #{isClientDefine}


        and t.sort =#{sort}


        ${params.dataScope}


order by t.sort asc, t.code asc

mapper.update:
update s_con_invoice_type
code = #{code}, name = #{name}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, is_client_define = #{isClientDefine}, sort = #{sort},
where sid = #{sid}

mapper.select:
select sid,code,name,status,handle_status,sort
from s_con_invoice_type


        and status = #{status}


        and handle_status = #{handleStatus}


order by sort asc, name asc
