mapper.select:



        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.labor_type_sid in
        (#{item})


        and t.labor_type_sid =#{laborTypeSid}


        and (t.labor_type_code like concat('%',
        #{item}, '%')
    )


        and (t.labor_type_name like
        concat('%',
        #{item}, '%')
    )


        and t.company_sid =#{companySid}


        and t.status =#{status}


        and (t.handle_status like
        concat('%',
        #{item}, '%')
    )


        and t.handle_status in
        (#{handleStatus})


        and (t3.nick_name like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        and t.sort =#{sort}


order by t.sort asc, t.labor_type_name asc

mapper.update:
update s_con_labor_type
labor_type_code = #{laborTypeCode}, labor_type_name = #{laborTypeName}, company_sid = #{companySid}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, sort = #{sort},
where labor_type_sid = #{laborTypeSid}

update.foreach:
update s_con_labor_type
client_id = #{clientId}, labor_type_code = #{laborTypeCode}, labor_type_name = #{laborTypeName}, company_sid = #{companySid}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, sort = #{sort},
where labor_type_sid = #{laborTypeSid}
