mapper.select:



        and t.client_id = #{clientId}


        and t.code = #{code}

    and
        t.name like concat('%', #{name}, '%')


        and t.sort = #{sort}


        and t.status = #{status}


        and t.department_type in (

            #{item}

        )


        and t.manager_sid = #{managerSid}


        and t.manager_sid in (#{item})


        and t.handle_status = #{handleStatus}


        and t.creator_account = #{creatorAccount}


        and t2.nick_name like CONCAT("%" , #{creatorAccountName} , "%")


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.creator_account desc

mapper.update:
update s_con_manufacture_department
client_id = #{clientId}, code = #{code}, name = #{name}, manager_sid = #{managerSid}, manager_code = #{managerCode}, sort = #{sort}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, department_type=#{departmentType},
where sid = #{sid}

update.foreach:
