mapper.select:



        and t.client_id = #{clientId}


        and t.manufacture_milestone_list_sid = #{manufactureMilestoneListSid}


        and t.sort = #{sort}


        and t.milestone = #{milestone}


        and t.creator_account = #{creatorAccount}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.sort asc

mapper.update:
update s_con_manufacture_milestone_list_item
sort = #{sort}, milestone = #{milestone}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate},
where manufacture_milestone_list_item_sid = #{manufactureMilestoneListItemSid}

update.foreach:
update s_con_manufacture_milestone_list_item
sort = #{o.sort}, milestone = #{o.milestone}, remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate},
where manufacture_milestone_list_item_sid = #{o.manufactureMilestoneListItemSid}
