mapper.select:



        and t.client_id = #{clientId}


        and t.manufacture_milestone_list_sid in
        (#{item})


        and t.manufacture_milestone_list_sid = #{manufactureMilestoneListSid}


        and t.milestone_type = #{milestoneType}


        and t.milestone_type in
        (#{item})


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{item})


        and t.creator_account = #{creatorAccount}


        and u1.nick_name like concat('%', #{creatorAccountName}, '%')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.create_date desc

mapper.update:
update s_con_manufacture_milestone_list
milestone_type = #{milestoneType}, remark = #{remark}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where manufacture_milestone_list_sid = #{manufactureMilestoneListSid}
