mapper.sql:
select t.client_id, t.movement_type_code, t1.name as movement_type_name, t1.sid as movement_type_sid, t.remark, t.status, t.handle_status, t.inv_doc_category_code, t.creator_account, t.create_date, t.updater_account, t.update_date, t.confirmer_account, t.confirm_date, t.data_source_sys, t.sort, t.is_client_define, t1.refer_unit_type, t.is_storehouse_edit, t.is_storehouse_location_edit, t1.material_flag, t1.is_use_reserve, t1.is_same_storehouse, t1.is_transfer_onestep, t1.is_same_storehouse, t1.special_stock, t2.name as special_stock_name from s_con_in_out_stock_doc_category t left join s_con_movement_type t1 on t1.code = t.movement_type_code left join s_con_special_stock t2 on t1.special_stock = t2.code
and t.inv_doc_category_code = #{code}
and t.doc_category_code = #{docCategoryCode}
and t1.is_transfer_onestep = #{isTransferOnestep}
and t1.material_flag is NULL

mapper.select:
select t.client_id, t.movement_type_code, t1.name as movement_type_name, t1.sid as movement_type_sid, t.remark, t.status, t.handle_status, t.inv_doc_category_code, t.creator_account, t.create_date, t.updater_account, t.update_date, t.confirmer_account, t.confirm_date, t.data_source_sys, t.sort, t.is_client_define, t1.refer_unit_type, t.is_storehouse_edit, t.is_storehouse_location_edit, t1.material_flag, t1.is_use_reserve, t1.is_same_storehouse, t1.is_transfer_onestep, t1.is_same_storehouse, t1.special_stock, t2.name as special_stock_name from s_con_in_out_stock_doc_category t left join s_con_movement_type t1 on t1.code = t.movement_type_code left join s_con_special_stock t2 on t1.special_stock = t2.code
and t1.material_flag is NULL
and t.inv_doc_category_code = #{invDocCategoryCode}
and t.doc_category_code = #{docCategoryCode}
and t1.is_transfer_onestep = #{isTransferOnestep}
and t1.sid in (#{item})

mapper.update:
update s_con_movement_type
client_id = #{clientId}, code = #{code}, name = #{name}, material_flag = #{materialFlag}, is_use_reserve = #{isUseReserve}, is_storehouse_edit = #{isStorehouseEdit}, is_storehouse_location_edit = #{isStorehouseLocationEdit}, special_stock = #{specialStock}, is_transfer_onestep = #{isTransferOnestep}, is_same_storehouse = #{isSameStorehouse}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, is_client_define = #{isClientDefine}, sort = #{sort}, is_allow_reverse = #{isAllowReverse}, refer_unit_type = #{referUnitType},
where sid = #{sid}

update.foreach:
update s_con_movement_type
client_id = #{clientId}, code = #{code}, name = #{name}, material_flag = #{materialFlag}, is_use_reserve = #{isUseReserve}, is_storehouse_edit = #{isStorehouseEdit}, is_storehouse_location_edit = #{isStorehouseLocationEdit}, special_stock = #{specialStock}, is_transfer_onestep = #{isTransferOnestep}, is_same_storehouse = #{isSameStorehouse}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, is_client_define = #{isClientDefine}, sort = #{sort}, is_allow_reverse = #{isAllowReverse}, refer_unit_type = #{referUnitType},
where sid = #{sid}

mapper.select:
select
client_id,
sid,
code,
name,
material_flag,is_storehouse_edit,is_storehouse_location_edit,
is_use_reserve,special_stock,is_transfer_onestep,is_same_storehouse,
status,
handle_status,
creator_account,
create_date,
sort,
refer_unit_type,
is_allow_reverse
from s_con_movement_type


        and material_flag = #{materialFlag}


        and is_storehouse_edit = #{isStorehouseEdit}


        and is_storehouse_location_edit = #{isStorehouseLocationEdit}


        and is_use_reserve = #{isUseReserve}


        and special_stock = #{specialStock}


        and is_transfer_onestep = #{isTransferOnestep}


        and is_same_storehouse = #{isSameStorehouse}


        and status = #{status}


        and handle_status = #{handleStatus}


        and is_allow_reverse = #{isAllowReverse}


        and t.refer_unit_type = #{referUnitType}


order by sort asc, name asc
