mapper.select:



        and t.client_id = #{clientId}


        and t.plant_sid = #{plantSid}


        and t.plant_sid in (

                #{plantSid}

        )


        and t.check_type in (

                #{checkType}

        )


        and t.plant_code like concat('%', #{plantCode}, '%')


        and t.product_price_type = #{productPriceType}


        and t.jixin_wangong_type = #{jixinWangongType}


        and t.enter_mode = #{enterMode}


        and t.check_type = #{checkType}


        and t.jiesuanshu_enter_sequence = #{jiesuanshuEnterSequence}


        and t.paichan_batch_null_remind_mode = #{paichanBatchNullRemindMode}


        and t.sort = #{sort}


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


        and t.creator_account = #{creatorAccount}


        and t2.nick_name like concat("%" , #{creatorAccountName} , "%")


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.create_date desc

mapper.update:
update s_con_plant_jixinliang_enter_mode
client_id = #{clientId}, plant_sid = #{plantSid}, plant_code = #{plantCode}, product_price_type = #{productPriceType}, jixin_wangong_type = #{jixinWangongType}, enter_mode = #{enterMode}, check_type = #{checkType}, jiesuanshu_enter_sequence = #{jiesuanshuEnterSequence}, paichan_batch_null_remind_mode = #{paichanBatchNullRemindMode}, sort = #{sort}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where sid = #{sid}

