mapper.select:



        and t.department in (

                #{item}

        )


        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.sid in
        (#{item})


        and t.sid= #{sid}


        and (t.code like concat('%',
        #{item}, '%')
    )


        and t.stage_type = #{stageType}

    and
        t.name like concat('%', #{name}, '%')


        and t.sort =#{sort}


        and (t.status like concat('%',
        #{item}, '%')
    )


        and (t.handle_status like
        concat('%',
        #{item}, '%')
    )


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


        AND t1.nick_name like concat("%" , #{creatorAccountName} , "%")


order by t.sort asc, t.name asc

mapper.update:
update s_con_produce_stage
sid = #{sid}, code = #{code}, name = #{name}, sort = #{sort}, stage_type = #{stageType}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, department = #{department},
where sid = #{sid}

update.foreach:
update s_con_produce_stage
client_id = #{clientId}, sid = #{sid}, code = #{code}, name = #{name}, sort = #{sort}, stage_type = #{stageType}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, department = #{department},
where sid = #{sid}
