mapper.select:



        and t.client_id = #{clientId}


        and t.is_default = #{isDefault}


        and t.tax_rate_sid in
        (#{item})


        and t.tax_rate_sid = #{taxRateSid}


        and t.tax_rate_code =#{taxRateCode}

    and
        t.tax_rate_name like concat('%', #{taxRateName}, '%')


        and t.tax_rate_value = #{taxRateValue}


        and t.sort =#{sort}


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


        and (t2.nick_name like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.sort asc, t.tax_rate_value asc

mapper.update:
update s_con_tax_rate
tax_rate_code = #{taxRateCode}, tax_rate_name = #{taxRateName}, tax_rate_value = #{taxRateValue}, is_default = #{isDefault}, sort = #{sort}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where tax_rate_sid = #{taxRateSid}

update.foreach:
update s_con_tax_rate
client_id = #{clientId}, tax_rate_code = #{taxRateCode}, tax_rate_name = #{taxRateName}, tax_rate_value = #{taxRateValue}, is_default = #{isDefault}, sort = #{sort}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where tax_rate_sid = #{taxRateSid}

mapper.select:
select tax_rate_sid,tax_rate_code,tax_rate_name,tax_rate_value,is_default,sort,status,handle_status
from s_con_tax_rate


        and status = #{status}


        and handle_status = #{handleStatus}


order by sort asc, tax_rate_value asc
