mapper.select:



        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and (t.business_category
        like concat('%',
        #{item}, '%')
    )


        and (t.title like concat('%',
        #{item}, '%')
    )

    and
        t.table_name like concat('%', #{tableName}, '%')


        and (t.document_sid like
        concat('%',
        #{item}, '%')
    )


        and t.menu_id =#{menuId}


        and t.expired_date = #{expiredDate}


        and t.notice_date = #{noticeDate}


        and t.user_id =#{userId}


        and t.user_id in (#{userId})


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        and (t.document_code like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.expired_date asc, t.document_code asc

mapper.update:
update s_sys_overdue_business
client_id = #{clientId}, business_category = #{businessCategory}, title = #{title}, table_name = #{tableName}, document_sid = #{documentSid}, menu_id = #{menuId}, expired_date = #{expiredDate}, notice_date = #{noticeDate}, user_id = #{userId}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, document_code = #{documentCode},
where overdue_business_sid = #{overdueBusinessSid}

update.foreach:
update s_sys_overdue_business
client_id = #{clientId}, business_category = #{businessCategory}, title = #{title}, table_name = #{tableName}, document_sid = #{documentSid}, menu_id = #{menuId}, expired_date = #{expiredDate}, notice_date = #{noticeDate}, user_id = #{userId}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, document_code = #{documentCode},
where overdue_business_sid = #{overdueBusinessSid}

mapper.delete:
DELETE FROM s_sys_overdue_business t


        and t.client_id = #{clientId}


        and t.table_name = #{tableName}


        and t.table_name in (#{item})
