mapper.select:



        and t.client_id = #{clientId}


        and t.task_category = #{taskCategory}


        and t.task_category in (#{item})


        and t.business_type = #{businessType}


        and t.business_type in (#{item})


        and (t.title like concat('%',
        #{item}, '%')
    )


        and t.table_name like concat('%', #{tableName}, '%')


        and t.document_sid = #{documentSid}


        and t.document_item_sid =#{documentItemSid}


        and t.menu_id =#{menuId}


        and t.notice_date = #{noticeDate}


        and t.user_id =#{userId}


        and t.user_id in (#{userId})


        and t.creator_account = #{creatorAccount}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.data_source_sys = #{dataSourceSys}


        and (t.document_code like
        concat('%', #{item}, '%'))


        ${params.dataScope}


order by t.notice_date desc, t.document_code asc

mapper.update:
update s_sys_todo_task
client_id = #{clientId}, todo_task_sid = #{todoTaskSid}, task_category = #{taskCategory}, business_type = #{businessType}, title = #{title}, table_name = #{tableName}, document_sid = #{documentSid}, menu_id = #{menuId}, notice_date = #{noticeDate}, user_id = #{userId}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, document_code = #{documentCode}, document_item_sid = #{documentItemSid},
where todo_task_sid = #{todoTaskSid}

update.foreach:
update s_sys_todo_task
client_id = #{o.clientId}, todo_task_sid = #{o.todoTaskSid}, task_category = #{o.taskCategory}, business_type = #{o.businessType}, title = #{o.title}, table_name = #{o.tableName}, document_sid = #{o.documentSid}, menu_id = #{o.menuId}, notice_date = #{o.noticeDate}, user_id = #{o.userId}, remark = #{o.remark}, creator_account = #{o.creatorAccount}, create_date = #{o.createDate}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, confirmer_account = #{o.confirmerAccount}, confirm_date = #{o.confirmDate}, data_source_sys = #{o.dataSourceSys}, document_code = #{o.documentCode}, document_item_sid = #{o.documentItemSid},
where todo_task_sid = #{o.todoTaskSid}

mapper.delete:
DELETE FROM s_sys_todo_task t


        and t.client_id = #{clientId}


        and t.title like concat('%', #{title}, '%')


        and t.task_category = #{taskCategory}


        and t.table_name = #{tableName}


        and t.table_name in (#{item})
